import { CommonModule } from '@angular/common';
import { Component, EventEmitter, Input, Output } from '@angular/core';
import { ModalComponent } from '../modal/modal.component';

@Component({
  standalone: true,
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.less'],
  imports: [CommonModule, ModalComponent]
})
export class HeaderComponent {
  @Input() addressData: any;
  @Input() businessAccount: boolean = false;
  @Output() menuStyling = new EventEmitter();

  isModalOpened: boolean = false;

  mobileStyling(): void {
    this.menuStyling.emit();
  }

}
<header class="header">
    <div class="container">
        <div class="header__wrapper">
            <a href="/" class="header__inner">
                <p class="caption">Щоб їсти GOOD</p>
                <p class="slogan">тицяй TUT</p>
            </a>
            <div class="address-login_block">
                <p class="address" *ngIf="addressData"><img src="assets/icons/location-icon.svg"
                        style="width: 25px; margin-right: 15px;" />{{ addressData }}</p>
                <button *ngIf="businessAccount === false" class="btn start" (click)="isModalOpened=true">Почати</button>
                <button *ngIf="businessAccount" class="btn start">Ресторан</button>
            </div>
            <div class="burger">
                <span>
                    <img src="assets/icons/user-icon.svg" style="width: 60px;" alt="">
                </span>
                <span></span>
                <span></span>
                <span></span>
            </div>
        </div>
    </div>
</header>
<app-modal *ngIf="isModalOpened" (closeModal)="isModalOpened=false">
</app-modal>
